#!/bin/bash
sudo su
yum update -y
adduser teacher
usermod -aG adm teacher
cd /home/teacher
mkdir .ssh
chgrp teacher .ssh
chown teacher .ssh
chmod 700 .ssh
touch authorized_keys
echo "ssh-rsa  andrey@MBP-Andrey">>authorized_keys
chgrp teacher authorized_keys
chown teacher authorized_keys
chmod 600 authorized_keys
mv authorized_keys .ssh/
